<template>
    <div class="modal-wrapper h-full w-full">
        <div>
            <h4 :style="{ textAlign: 'center' }">Quick Connect with <b class="">WhatsApp</b></h4>
            <div class="modal-container" v-if="isAdmin">
                <div class="modal">
                    <ol>
                        <li>
                            <p>Enter the WhatsApp number that you want to connect on the right and open WhatsApp on your device</p>
                        </li>
                        <li :style="{ marginTop: '3px' }">
                            <p>Tap Menu ⋮ and click on ‘Linked devices’️</p>
                        </li>
                        <li :style="{ marginTop: '3px' }">
                            <p>Click on ‘Link a device’</p>
                        </li>
                        <li :style="{ marginTop: '3px' }">
                            <p>Scan the QR code or click on 'Link with phone number instead' and then enter the code generated</p>
                        </li>
                    </ol>
                </div>
                <div class="modal">
                    <p class="text-center"><b>Enter the WhatsApp number</b></p>
                    <phone-input v-model="phoneNumber" @setCode="ct_code = $event" />
                    <woot-button @click="generateCode(currentUser.access_token, currentAccountId)"
                        :disabled="isGeneratingCode">
                        {{ buttonText }}
                    </woot-button>
                    <div class="img-container">
                        <img class="w-full h-full" :src="dynamicQR" :style="{ opacity: !isGeneratingCode ? '0.5' : '1' }"/>
                    </div>
                    <p>Your code: <br><strong>{{ code }}</strong></p>
                </div>
            </div>
            <EmptyState v-else />
        </div>
    </div>
</template>

<script>
import axios from 'axios';
import wootButton from 'dashboard/components/buttons/Button.vue';
import PhoneInput from 'dashboard/components/widgets/forms/PhoneInput.vue';
import { addBusinessDays } from 'date-fns';
import io from 'socket.io-client';
import { mapGetters } from 'vuex';
import adminMixin from '../../../mixins/isAdmin';
import EmptyState from './EmptyState.vue'; // Replace with the correct import path

export default {
    mixins: [adminMixin],
    components: {
        wootButton,
        PhoneInput,
        EmptyState,
    },
    data() {
        return {
            showQuickConnectPopup: true,
            phoneNumber: '',
            code: '',
            socket: null,
            dynamicQR: 'data:image/png;base64,',
            buttonText: 'Connect',
            isGeneratingCode: false,
            ct_code: '',
            new_phone: '',
        };
    },
    computed: {
        ...mapGetters({
            currentUser: 'getCurrentUser',
            getAccount: 'accounts/getAccount',
            currentAccountId: 'getCurrentAccountId',
        }),
    },
    methods: {
        openQuickConnectPopup() {
            this.showQuickConnectPopup = true;
            bus.$emit('newToastMessage', currentUser);
        },
        closeQuickConnectPopup() {
            this.showQuickConnectPopup = false;
        },
        async generateCode(token, AccountId) {
            this.isGeneratingCode = true;

            const data = {
                instanceName: this.phoneNumber,
                token: "",
                qrcode: false,
                //number: `${this.phoneNumber}@s.whatsapp.net`,
                chatwoot_account_id: AccountId,
                chatwoot_token: token,
                chatwoot_url: "https://bow.boni.co.in",
                chatwoot_sign_msg: false,
                chatwoot_reopen_conversation: true,
                chatwoot_conversation_pending: false
            };
            if (this.ct_code === null || this.ct_code === '') {
                this.phoneNumber = '91' + this.phoneNumber;
            } else {
                let parts = this.ct_code.split('+')[1];
                console.log(parts)
                this.phoneNumber = `${parts}` + `${this.phoneNumber}`
            }

            try {
                const response = await axios.post('https://instance.boni.co.in/instance/create', data, {
                    headers: {
                        'apikey': 'B6D711FCDE4D4FD5936544120E713976'
                    }
                });

                let apikey = response.data.hash.apikey;
                let instance_name = response.data.instance.instanceName;

                let attempts = 0;

                setTimeout(async () => {
                    const connect_response = await axios.get(`https://instance.boni.co.in/instance/connect/${instance_name}?number=${this.phoneNumber}`, {
                        headers: { 'apikey': apikey }
                    });

                    this.code = connect_response.data.pairingCode;

                    if (this.code === null) {
                        this.code = "The pairing code was null";
                    }

                    this.dynamicQR = connect_response.data.base64;

                    if (connect_response.data.instance && connect_response.data.instance.state === 'open') {
                        clearInterval(intervalId);
                        this.isGeneratingCode = false;
                        this.phoneNumber = '';
                        return;
                    }



                    attempts++;

                    let intervalId = setInterval(async () => {
                        if (attempts >= 5) {
                            clearInterval(intervalId);
                            this.isGeneratingCode = false

                            this.buttonText = 'Reconnect'
                            return;
                        }

                        const connect_response = await axios.get(`https://instance.boni.co.in/instance/connect/${instance_name}?number=${this.phoneNumber}`, {
                            headers: { 'apikey': apikey }
                        });

                        this.code = connect_response.data.pairingCode;

                        if (this.code === null) {
                            this.code = "The pairing code was null";
                        }

                        this.dynamicQR = connect_response.data.base64;

                        attempts++;
                    }, 40000);
                }, 2000);

            } catch (error) {
                if (error.response.status === 403) {

                    if (this.phoneNumber.startsWith('91')) {
                        this.new_phone = this.phoneNumber.substring(2);
                    }

                    let url_fetch = `https://instance.boni.co.in/instance/fetchInstances?instanceName=${this.new_phone}`;

                    let fetch_data = await axios.get(url_fetch, {
                        headers: { 'apikey': 'B6D711FCDE4D4FD5936544120E713976' }
                    });

                    let apikey = fetch_data.data.instance.apikey;
                    let instance_name = fetch_data.data.instance.instanceName;

                    let attempts = 0;

                    setTimeout(async () => {
                        const connect_response = await axios.get(`https://instance.boni.co.in/instance/connect/${instance_name}?number=${this.phoneNumber}`, {
                            headers: { 'apikey': apikey }
                        });

                        this.code = connect_response.data.pairingCode;

                        if (this.code === null) {
                            this.code = "The pairing code was null";
                        }

                        this.dynamicQR = connect_response.data.base64;
                        if (connect_response.data.instance && connect_response.data.instance.state === 'open') {
                            clearInterval(intervalId);
                            this.isGeneratingCode = false;
                            this.phoneNumber = '';
                            return;
                        }

                        attempts++;

                        let intervalId = setInterval(async () => {
                            if (attempts >= 5) {
                                clearInterval(intervalId);
                                this.isGeneratingCode = false

                                this.buttonText = 'Reconnect'
                                return;
                            }

                            const connect_response = await axios.get(`https://instance.boni.co.in/instance/connect/${instance_name}?number=${this.phoneNumber}`, {
                                headers: { 'apikey': apikey }
                            });

                            this.code = connect_response.data.pairingCode;

                            if (this.code === null) {
                                this.code = "The pairing code was null";
                            }

                            this.dynamicQR = connect_response.data.base64;

                            attempts++;
                        }, 40000);
                    }, 2000);
                }
            }


        }
    },
    mounted() {
    },
};
</script>

